function restartState(){buttons.play.disabled=!1,buttons.pause.disabled=!0,buttons.stop.disabled=!0}function playVideo(){buttons.play.disabled=!0,buttons.pause.disabled=!1,buttons.stop.disabled=!1,video.play()}function pauseVideo(){buttons.play.disabled=!1,buttons.pause.disabled=!0,buttons.stop.disabled=!1,video.pause()}function stopVideo(){video.pause(),video.currentTime=0,restartState()}buttons={play:document.getElementById("play"),pause:document.getElementById("pause"),stop:document.getElementById("stop")},video=document.querySelector("video"),buttons.play.addEventListener("click",playVideo,!1),buttons.pause.addEventListener("click",pauseVideo,!1),buttons.stop.addEventListener("click",stopVideo,!1),restartState();